use common::model::timezone::NUM_TIMEZONES_WITH_GLOBAL_CATEGORY;
use common::model::types::DayId;
use common::model::types::MonthId;
use common::model::types::WeekId;

/**
 * Global time period ids across timezones, maintained at the same time as data is moved
 * in timezone chunks between, current and past (and future).
 *
 * Used to verify client requests, to make sure that their requests are still valid.
 */
pub struct TimePeriodIds {
    pub last_month: [MonthId; NUM_TIMEZONES_WITH_GLOBAL_CATEGORY as usize],
    pub this_month: [MonthId; NUM_TIMEZONES_WITH_GLOBAL_CATEGORY as usize],
    pub next_month: [MonthId; NUM_TIMEZONES_WITH_GLOBAL_CATEGORY as usize],
    pub last_week: [WeekId; NUM_TIMEZONES_WITH_GLOBAL_CATEGORY as usize],
    pub this_week: [WeekId; NUM_TIMEZONES_WITH_GLOBAL_CATEGORY as usize],
    pub next_week: [WeekId; NUM_TIMEZONES_WITH_GLOBAL_CATEGORY as usize],
    pub day_b4_yesterday: [DayId; NUM_TIMEZONES_WITH_GLOBAL_CATEGORY as usize],
    pub yesterday: [DayId; NUM_TIMEZONES_WITH_GLOBAL_CATEGORY as usize],
    pub today: [DayId; NUM_TIMEZONES_WITH_GLOBAL_CATEGORY as usize],
    pub tomorrow: [DayId; NUM_TIMEZONES_WITH_GLOBAL_CATEGORY as usize],
    pub day_after_tomorrow: [DayId; NUM_TIMEZONES_WITH_GLOBAL_CATEGORY as usize],
}

